import React from 'react';
import { Link, useLocation } from 'react-router-dom';

function Menu() {
    const location = useLocation();
    const activeTab = location.pathname.split('/').pop(); // Extract the last part of the URL

    const tabs = [
        { label: 'Home', path: '/' },
        { label: 'Sign Up', path: '/user/signup' },
        { label: 'Map', path: '/user/map' },
        { label: 'Account', path: '/user/account' },
        { label: 'Settings', path: '/user/settings' },
    ];

    return (
        <section className="container" style={{ paddingRight: 0, paddingLeft: 0 }}>
            <div className="hero">
                <div className="container h-100">
                    <div className="row h-100">
                        <div className="col-md-6 col-xl-8 text-center text-md-start d-flex d-sm-flex d-md-flex justify-content-center align-items-center justify-content-md-start align-items-md-center justify-content-xl-center">
                            <div style={{ maxWidth: "none", width: "95%" }}>
                                <div className="card" style={{ width: "auto" }}>
                                    <div className="container custom-spaceTop">
                                        <Link className="navbar-brand d-flex align-items-center" to="/">
                                            <span style={{ fontWeight: "bold", color: "#ffffff" }}>The Coldest Market</span>
                                        </Link>
                                        <ul className="nav nav-tabs nav-justified card-header-tabs" style={{ listStyleType: 'none', color: 'white', padding: 0 }}>
                                            {tabs.map((tab) => (
                                                <li key={tab.label} className="nav-item">
                                                    <Link
                                                        to={tab.path}
                                                        className={`nav-link ${activeTab === tab.label ? 'active' : ''}`}
                                                    >
                                                        {tab.label}
                                                    </Link>
                                                </li>
                                            ))}
                                        </ul>
                                    </div>
                                    <div className="card-body">
                                        {/* Content based on the active tab */}
                                        <h5>{activeTab} Content</h5>
                                        {/* Add content specific to each page */}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Menu;
